jQuery(document).ready(function(t){t(".like-dislike-rating button").on("click",function(){var o=t(this).closest(".like-dislike-rating"),s=o.data("post-id"),a=t(this).data("vote"),n=o.find(".ldr_nonce").val();t.ajax({url:LDR.ajax_url,type:"POST",data:{action:"update_rating",post_id:s,vote:a,nonce:n},success:function(i){if(i.success){var c=i.data.rating,e=o.find(".rating-value");e.text(c),c>0?e.css("color","green"):c<0?e.css("color","red"):e.css("color","")}}})})});
